import React from 'react';
import { Container, Typography, Box, Grid, Paper } from '@mui/material';
import { Document, Page, pdfjs } from 'react-pdf';
import 'react-pdf/dist/esm/Page/AnnotationLayer.css';

// Set the workerSrc to the correct path
pdfjs.GlobalWorkerOptions.workerSrc = `//cdnjs.cloudflare.com/ajax/libs/pdf.js/${pdfjs.version}/pdf.worker.min.js`;

function About() {
  return (
    <Box sx={{ flexGrow: 1, bgcolor: 'background.default', pt: 8, pb: 6 }}>
      <Container maxWidth="lg">
        <Typography
          component="h1"
          variant="h2"
          align="center"
          color="text.primary"
          gutterBottom
        >
          About NeuroAI
        </Typography>
        <Typography variant="h5" align="center" color="text.secondary" paragraph>
          Revolutionizing medical training with cutting-edge AI technology
        </Typography>

        <Grid container spacing={4} sx={{ mt: 4 }}>
          {gridItems.map((item, index) => (
            <Grid item xs={12} sm={6} md={4} key={index}>
              <Paper sx={{
                padding: 2,
                textAlign: 'center',
                color: 'text.secondary',
                height: '100%',
                display: 'flex',
                flexDirection: 'column',
                justifyContent: 'space-between',
              }}>
                <Typography variant="h6" component="h2" gutterBottom>
                  {item.title}
                </Typography>
                <Typography>{item.description}</Typography>
              </Paper>
            </Grid>
          ))}
        </Grid>

        {/* New Section: Read the Curriculum */}

      </Container>
    </Box>
  );
}

const gridItems = [
  {
    title: "AI-Powered Learning",
    description: "Harness the power of artificial intelligence to enhance neurological education and training."
  },
  {
    title: "Personalized Curriculum",
    description: "Adaptive learning paths tailored to individual student needs and progress."
  },
  {
    title: "Real-world Simulations",
    description: "Immersive, AI-driven scenarios that mimic real clinical situations."
  },
  {
    title: "Advanced Diagnostics",
    description: "Utilize AI algorithms to assist in complex neurological diagnoses and treatment planning."
  },
  {
    title: "Continuous Learning",
    description: "Stay updated with the latest advancements in neurology through our AI-driven content delivery system."
  },
  {
    title: "Collaborative Platform",
    description: "Connect with peers and experts worldwide to share knowledge and experiences in neurological care."
  },
  {
    title: "Why NeuroAI?",
    description: "NeuroAI combines the latest advancements in artificial intelligence with expert knowledge in neurology. Our platform prepares medical professionals for the challenges of tomorrow by providing cutting-edge, AI-enhanced training today."
  },
  {
    title: "Template Title 1",
    description: "This is a placeholder description for the template tile. Customize it as needed."
  },
  {
    title: "Template Title 2",
    description: "This is another placeholder description for the template tile. Customize it as needed."
  }
];

export default About;
